import {useState} from 'react';

function Square(){
	const [value, setValue] = useState(null); //記憶させる

	function handleClick(){
		setValue('X');
	}

	return (
		<button 
			className="square"
			onClick={handleClick}	
		>
			{value}
		</button>
	);
	}


export default function Board(){
	return (
	<>
	<div className="board-row">
		<Square />
		<Square />
		<Square />
	</div>
	<div className="board-row">
		<Square />
		<Square />
		<Square />
	</div>
	<div className="board-row">
		<Square />
		<Square />
		<Square />
	</div>
	</>
	);
}
